import React, { Component } from 'react'
import styled from 'styled-components'
import Link from 'next/link'

// const Block = styled.div`
//   display: none;
//   @media (max-width: 768px){
//     display: bolck;
//   }
// `

const BgIcon = styled.div`
  width: 100%;
  padding: 10px 0;
  background: #263843;
  display: flex;
  color: #fff;
  text-align: center;
  border-bottom: 1px solid #707070;
`
const BgIconList = styled.div`
  width: 100%;
  padding: 10px 0;
  background: #3B4B55;
  display: flex;
  color: #fff;
  text-align: center;
  border-bottom: 1px solid #707070;
`
const Icon = styled.div`
  width: 35px;
  & > img {
    width: 100%;
  }
`
const IconTwo = styled.div`
  width: 33px;
  & > img {
    width: 100%;
  }
`
const FooterList = styled.div`
  width:100%;
  display: flex;
  justify-content: space-between;
  margin: 10px 0;
`
const FooterTag = styled.div`
  text-align: center;
  width: ${props => props.long ? '35%' : '30%'};
`

const FooterTitle = styled.div`
  width:90%;
  margin:0 auto;
  margin-bottom: 50px;
`
const Footer = styled.div`
  width: 100%;
  text-align: center;
  color: #fff;
  & > h5 {
    font-weight: 400;
    margin: 15px 0;
  }
  & > h6 {
    color: #fff;
    font-size: 12px;
    margin: 5px 0;
  }
`
const FooterLogo = styled.div`
  width: 60%;
  margin: 0 auto;
  margin-top: 30px;
  margin-bottom: 30px;
  & > img {
    width: 100%;
  }
`
const BtnFooter = styled.div`
  width: 100%;
  display: flex;
  background: #263843;
  position: fixed;
  bottom:0;
  z-index: 1;
`
const DrowDown = styled.ul`
  padding:0px 10px;
`
const DownItem = styled.li`
  padding: 10px 0;
  color: #D2D2D2;
  text-align: left;
  font-size: 16px;
  display: flex;
  justify-content: space-between;
  & > a{
    color: #D2D2D2 !important;
  }
`
const Button = styled.div`
  width: 60%;
  text-align: center;
  cursor: pointer;
  color: #fff;
  padding:15px 20px;
  font-size: 20px;
  display: flex;
  justify-content: space-between;
  background-color: #fff;
  & > img {
    width: 20%;
  }
`
const Button2 = styled.div`
  width: 40%;
  text-align: center;
  cursor: pointer;
  color: #fff;
  padding:15px;
  font-size: 18px;
  display: flex;
  // justify-content: space-between;
  & > img {
    width: 20%;
  }
`
const BtnFix = styled.div`
  margin-top: 40px;
`

class MobileFooter extends Component {
  constructor(){
    super()
    
  }

  render(){
    return(
      <div className='mbFooter'>
        <BgIconList>
          <FooterTitle>
            <FooterList>
              <FooterTag className='textCenter'>主題特輯</FooterTag>
              <FooterTag className='textCenter'>買賣</FooterTag>
              <FooterTag className='textCenter'>租賃</FooterTag>
            </FooterList>
            <FooterList>
              <FooterTag className='textCenter'>標售</FooterTag>
              <FooterTag className='textCenter'>新聞與研究</FooterTag>
              <FooterTag className='textCenter'>團隊介紹</FooterTag>
            </FooterList>
            <FooterList>
              <FooterTag className='textCenter'>關於全球</FooterTag>
              <FooterTag long className='textCenter'>集團與社會責任</FooterTag>
              <FooterTag>&nbsp;</FooterTag>
            </FooterList>
          </FooterTitle>
        </BgIconList>
        <BgIcon>
          <Footer>
            <h6>信義全球資產管理股份有限公司</h6>
            <h6>&copy;2019 信義房屋股份有限公司 版權所有</h6>
          </Footer>
        </BgIcon>
        <BtnFooter>
          <Button>
            <Icon>
              <img src='/static/img/icon_messenger.png' />
            </Icon>
            <Icon>
              <img src='/static/img/icon_fb.png' />
            </Icon>
            <IconTwo>
              <img src='/static/img/icon_ig.png' />
            </IconTwo>
          </Button>
        </BtnFooter>
      </div>
    )
  }
  
}

export default MobileFooter
