import React from 'react'
import styled from 'styled-components'
import Link from 'next/link'
import { Container, Row } from 'react-bootstrap'
import NewsItem from './newsItem'
import NewsItem2 from './newsItem2'

const NewsContent = styled.div`
  display: flex;
  margin: 30px 0;
  justify-content: space-between;
  flex-wrap: wrap;
  @media (max-width: 768px){
    margin: 10px 10px;
  }
`
const Left = styled.div`
  position: relative;
  width: 66.5%;
  margin-right: 3px;
  @media (max-width: 768px){
    display: none;
  }
`
const Right = styled.div`
  position: relative;
  width: 32.8%;
  height: 200px;
  margin-bottom: 3px;
  @media (max-width: 768px){
    display: none;
  }
`
const Other = styled.div`
  width: 32%;
  font-size: 20px;
  @media (max-width: 768px){
    width:100%;
  }
`
const BtnContent = styled.div`
  width:100%;
  text-align: center;
  padding: 20px 0 60px 0;
`
const Button = styled.div`
  width:15%; 
  margin: 0 auto;
  padding: 9px;
  text-align: center;
  cursor: pointer;
  font-size: 22px;
  color: #fff;
  border-radius: 50px;
  background-color: #008F53;
  @media (max-width: 768px){
    width:45%;
    padding: 10px;
    font-size: 16px;
  }
`


const NewsList = (props) => {
    return(
      <Container>
        <Row>
          <NewsContent>
            <Left>
              <NewsItem />
            </Left>
            <Right>
              <NewsItem />
              <NewsItem />
            </Right>
          </NewsContent>
          <NewsContent>
            <Other>
             <NewsItem2 title='女利崛起成就不凡 信義房屋女性同仁近四成' />
            </Other>
            <Other>
             <NewsItem2 title='信義企業集團 人力資料登錄表' />
            </Other>
            <Other>
             <NewsItem2 title='企管、財金畢業生加入房仲業人數倍增 信義房屋大舉徵才 從零做起' />
            </Other>
          </NewsContent>
          <BtnContent>
            <Link href='/newsList'>
              <Button>更多消息</Button>
            </Link>
            {/* <a href='/newsList'>
              <Button>更多消息</Button>
            </a> */}
          </BtnContent>
        </Row>
      </Container>
    )
}

export default NewsList
