import React from 'react'
import styled from 'styled-components'

const Content = styled.div`
  position: relative;
  width: 100%;
  margin-bottom: 6px;
  transition: all .5s;
  cursor: pointer;
`
const NewsImg = styled.div`
  width: 100%;
  overflow: hidden;
  object-fit: cover;
`
const NewsPhoto = styled.img`
  width: 100%;
  transition: all .5s;
  -webkit-filter: grayscale(10%); /* Safari */
  filter: grayscale(10%);
`
const NewsTitle = styled.div`
  position: absolute;
  pointer-events: none;
  z-index: 1;
  width: 100%;
  bottom: 0;
  color: #fff;
  padding: 20px;
  transition: all .5s;
`
const NewsCategory = styled.div`
  width: 70px;
  background: #263843;
  color: #fff;
  text-align: center;
  margin: 10px 0;
  padding: 1px 0;
  font-size: 16px;
  transition: all .5s;
`
const MyEllipsis = styled.h3`
  overflow:hidden;
  white-space: nowrap;
  text-overflow: ellipsis;
  display: -webkit-box;
  -webkit-line-clamp: 2;
  -webkit-box-orient: vertical;
  white-space: normal;
  font-size: 20px;
`

const NewsItem = (props) => {
    return(
      <Content className="newsDiv">
        <NewsImg className="newsImg">
          <NewsPhoto className="newsPhoto" src="static/img/career1.jpg" alt="" />
        </NewsImg>
        <NewsTitle className="newsTitle">
          <NewsCategory className="newsCategory">徵才</NewsCategory>
          <MyEllipsis className="myEllipsis">「我的我們次方」算出最大價值的幸福方程式 信義房屋攜手熱忱的你共創最大價值</MyEllipsis>
        </NewsTitle>
      </Content>
    )
}

export default NewsItem
