import React from 'react'
import styled from 'styled-components'
import Head from '../components/head'
import Layout from '../components/layout'
import Navbar from '../components/nav/navBar'
import NavHomeMobile from '../components/nav/navHomeMobile'
import FastButton from '../components/fastButton'
import '../style/index.css'
import 'bootstrap/dist/css/bootstrap.min.css';

const MainContain = styled.div`
  width: 100%;
  padding: 150px 0 50px 0;
`
const Container = styled.div`
  width: 85%;
  margin: 0 auto;
`
const Title = styled.div`
  text-align: center;

  & > h1{
    font-weight: bold;
    font-size: 28px;
  }
`
const TitleImg = styled.img`
  width: 100%;
`
const Content = styled.div`
  width: 100%;
  margin-top: 45px;
  border-bottom: 1px solid #E8E8E8;
`
const ConTitle = styled.div`
  border-left: 2px solid #00B1FF;
  padding-left: 8px;
  font-size: 24px;
  color: #00B1FF;
`
const ConText = styled.div`
  color: #5A5A5A;
  font-size: 18px;
`

const Bid = () => {
  return (
    <Layout>
      <Head
        title="信義全球資產"
        description="信義房屋集團人才招募培訓中，提供各種集團熱門職缺、徵才消息、薪資福利。完整新人培訓課程不用怕沒人可以問，歡迎熱忱的你加入我們，與我們一起共創最大價值。"
      />
      <Navbar />
      <NavHomeMobile />
      <MainContain>
        <Container>
          <Title>
            <h1>信義全球資產管理(股)公司</h1>
            <h1>臺北市精華地段辦公標售案</h1>
            <TitleImg src='/static/img/pic10.png' />
          </Title>
          <Content>
            <ConTitle>甲標標售調整公告 （民國107年11月1日）</ConTitle>
            <ConText>
              龍巖股份有限公司委託信義全球資產辦理「太平洋商業大樓」（甲標）公開標售調整議價案，詳如說明。<br />
              對於本次修正，如有任何疑問，請來電洽詢。
            </ConText>
          </Content>
        </Container>
      </MainContain>
      <FastButton />
    </Layout>
  )
}

export default Bid
