import React from 'react'
import styled from 'styled-components'

const Content = styled.div`
  position: relative;
  width: 100%;
  margin-bottom: 6px;
  transition: all .5s;
  cursor: pointer;
  box-shadow: 2px 10px 11px rgba(0, 0, 0, 0.2);
`
const NewsImg = styled.div`
  width: 100%;
  overflow: hidden;
  object-fit: cover;
`
const NewsPhoto = styled.img`
  width: 100%;
  transition: all .5s;
`
const NewsTitle = styled.div`
  position: absolute;
  pointer-events: none;
  z-index: 1;
  width: 100%;
  bottom: 0;
  color: #fff;
  padding: 20px;
  transition: all .5s;
`
const NewsCategory = styled.div`
  width: 70px;
  background: #263843;
  color: #fff;
  text-align: center;
  margin: -12px 0;
  padding: 0px 0;
  font-size: 16px;
  transition: all .5s;
  @media (max-width: 768px){
    margin: 10px 0;
    padding: 1px 0;
    width: 60px;
    font-size: 13px;
  }
`
const MyEllipsis = styled.h3`
  overflow:hidden;
  white-space: nowrap;
  text-overflow: ellipsis;
  display: -webkit-box;
  -webkit-line-clamp: 2;
  -webkit-box-orient: vertical;
  white-space: normal;
  color:#5A5A5A;
  font-size: 20px;
  @media (max-width: 768px){
    display: none;
  }
`
const MyEllipsisMb = styled.div`
  overflow:hidden;
  white-space: nowrap;
  text-overflow: ellipsis;
  display: none;
  -webkit-line-clamp: 2;
  -webkit-box-orient: vertical;
  white-space: normal;
  font-size: 20px;
  @media (max-width: 768px){
    display: -webkit-box;
    font-weight: 500;
    font-size: 16px;
  }
`

const NewsItem = (props) => {
  return(
    <>
      <Content className="newsDiv">
        <NewsImg className="newsImg">
          <NewsPhoto className="newsPhoto" src="static/img/nodata.png" alt="" />
        </NewsImg>
        <NewsTitle className="newsTitle">
          <NewsCategory className="newsCategory">徵才</NewsCategory>
          <MyEllipsisMb className="myEllipsis">{props.title}</MyEllipsisMb>
        </NewsTitle>
      </Content>
      <MyEllipsis className="myEllipsis">{props.title}</MyEllipsis>
    </>

  )
}

export default NewsItem
