import React, { Component } from 'react'
import styled from 'styled-components'
import Link from 'next/link'

// const Block = styled.div`
//   display: none;
//   @media (max-width: 768px){
//     display: bolck;
//   }
// `

const BgIcon = styled.div`
  width: 100%;
  padding: 10px 0;
  background: #263843;
  display: flex;
  color: #fff;
  text-align: center;
  border-bottom: 1px solid #707070;
`
const BgIconList = styled.div`
  width: 100%;
  padding: 10px 0;
  background: #3B4B55;
  display: flex;
  color: #fff;
  text-align: center;
  border-bottom: 1px solid #707070;
`
const Icon = styled.div`
  width: 35px;
  & > img {
    width: 100%;
  }
`
const IconTwo = styled.div`
  width: 33px;
  & > img {
    width: 100%;
  }
`
const People = styled.div`
  width: 23px;
  margin-left: 8px;
  margin-right: 8px;
  & > img {
    width: 100%;
  }
`

const FooterTitle = styled.div`
  width:90%;
  margin:0 auto;
  margin-bottom: 50px;
`
const Title = styled.div`
  text-align: left;
  padding: 20px 10px;
  border-bottom: 1px solid #01AB59;
  font-size: 18px;
  cursor: pointer;
  display: flex;
  justify-content: space-between;
  & > img {
    width: 100%;
  }
`
const Drow = styled.div`
  width: 10px;
  & > img {
    width: 100%;
  }
`
const Footer = styled.div`
  width: 100%;
  text-align: center;
  color: #fff;
  & > h5 {
    font-weight: 400;
    margin: 15px 0;
  }
  & > h6 {
    color: #fff;
    font-size: 12px;
    margin: 5px 0;
  }
`
const FooterLogo = styled.div`
  width: 60%;
  margin: 0 auto;
  margin-top: 30px;
  margin-bottom: 30px;
  & > img {
    width: 100%;
  }
`
const BtnFooter = styled.div`
  width: 100%;
  display: flex;
  background: #263843;
  position: fixed;
  bottom:0;
  z-index: 1;
`
const DrowDown = styled.ul`
  padding:0px 10px;
`
const DownItem = styled.li`
  padding: 10px 0;
  color: #D2D2D2;
  text-align: left;
  font-size: 16px;
  display: flex;
  justify-content: space-between;
  & > a{
    color: #D2D2D2 !important;
  }
`
const Button = styled.div`
  width: 60%;
  text-align: center;
  cursor: pointer;
  color: #fff;
  padding:15px 20px;
  font-size: 20px;
  display: flex;
  justify-content: space-between;
  background-color: #fff;
  & > img {
    width: 20%;
  }
`
const Button2 = styled.div`
  width: 40%;
  text-align: center;
  cursor: pointer;
  color: #fff;
  padding:15px;
  font-size: 18px;
  display: flex;
  // justify-content: space-between;
  & > img {
    width: 20%;
  }
`
const BtnFix = styled.div`
  margin-top: 40px;
`

class MobileFooter extends Component {
  constructor(){
    super()
    this.state = {
      displayMenu1: false,
      displayMenu2: false,
      displayMenu3: false,
      displayMenu4: false,
      displayMenu5: false,
    }
  }
  showDropdownMenu1 = (event) => {
    event.preventDefault();
    this.setState({ displayMenu1: !this.state.displayMenu1 });
  }
  showDropdownMenu2 = (event) => {
    event.preventDefault();
    this.setState({ displayMenu2: !this.state.displayMenu2 });
  }
