    padding: 20px 0 40px 0;
    padding-bottom: 20px;
  }
`
const MainNews = styled.div`
  width: 90%;
  display: flex;
  margin: 30px auto;
  justify-content: space-between;
  flex-wrap: wrap;
  @media (max-width: 768px){
    margin: 10px 10px;
  }
`
const TitleImg = styled.img`
  width:25%;
  @media (max-width: 768px){
    width:60%;
  }
`
const Img = styled.div`
  position: relative;
  width: 45%;
`
const Introduction = styled.div`
  padding:10px 20px;
  background:#fff;
  width: 45%;
`
const Date = styled.div`
  width: 100%;
  color: #B4B4B4;
  font-size: 20px;
  margin:10px 0;
`
const Title = styled.div`
  overflow:hidden;
  text-overflow: ellipsis;
  display: -webkit-box;
  -webkit-line-clamp: 2;
  -webkit-box-orient: vertical;
  white-space: normal;
  font-size: 24px;
  font-weight: bold;
`
const NewsSub = styled.div`
  overflow:hidden;
