  display: -webkit-box;
  -webkit-line-clamp: 3;
  -webkit-box-orient: vertical;
  white-space: normal;
  font-size: 18px;
  font-weight: 300;
  color: #5A5A5A;
  margin:10px 0 20px 0;
`
const Other = styled.div`
  width: 32%;
  font-size: 20px;
  @media (max-width: 768px){
    width:100%;
  }
`
const BtnContent = styled.div`
  width:100%;
  text-align: center;
  padding: 20px 0 60px 0;
`
const Show = styled.div`
  width: 100%;
  text-align: center;
  margin-bottom: 80px;
`
const Hr = styled.hr`
  width: 100%;
  margin: 0 auto;
  border: 1px solid #969696;
`


const StudyList = (props) => {
    return(
      <NewsContainer>
        <BigTitle>
          <TitleImg src='/static/img/t2_job.png' />
        </BigTitle>
        <Content>
          <NewsContent>
            <Other>
             <StudyCard />
            </Other>
            <Other>
             <StudyCard />
            </Other>
          </NewsContent>
          <BtnContent>
            <Show>
              show all
            </Show>
          </BtnContent>
        </Content>
      </NewsContainer>
    )
}

export default StudyList
