import React from 'react'
import styled from 'styled-components'
import Head from '../components/head'
import Layout from '../components/layout'
import Navbar from '../components/nav/navBar'
import NavHomeMobile from '../components/nav/navHomeMobile'
import OtherNewsCard from '../components/Card/otherNewsCard'
import FastButton from '../components/fastButton'
import '../style/index.css'
import 'bootstrap/dist/css/bootstrap.min.css';

const MainContain = styled.div`
  width: 100%;
  padding: 150px 0 50px 0;
  background: #fff;
`
const Container = styled.div`
  width: 80%;
  margin: 0 auto;
  @media (max-width: 768px){
    width: 100%;
  }
`
const Title = styled.div`
  text-align: center;

  & > h1{
    font-weight: bold;
    font-size: 28px;
  }
  @media (max-width: 768px){
    & > h1{
      font-weight: bold;
      font-size: 26px;
    }
  }
`
const NewsDate = styled.div`
  text-align: right;
  color: #B4B4B4;
  font-size: 16px;
`
const TitleImg = styled.img`
  width: 100%;
  @media (max-width: 768px){
    margin-top: 17px;
  }
`
const Content = styled.div`
  width: 100%;
  margin-top: 45px;
  @media (max-width: 768px){
    width: 90%;
    margin: 0 auto;
  }
`
const ConTitle = styled.div`
  border-left: 4px solid #00B1FF;
  padding-left: 8px;
  font-size: 24px;
  color: #00B1FF;
  @media (max-width: 768px){
    font-size: 18px;
    margin-top: 10px;
  }
`
const ConText = styled.div`
  color: #5A5A5A;
  font-size: 18px;
  padding: 10px 15px;
  margin-top: 11px;
  & > p {
    margin-bottom: 0;
  }
  @media (max-width: 768px){
    font-size:12px;
  }
`
const StatisticsImg = styled.div`
  width: 50%;
  margin: 15px 0 7px 5px;
  & > img {
    width: 100%;
  }
`
const DataFrom = styled.div`
  font-size: 16px;
  color: #969696;
`
const OtherNewsContent = styled.div`
  width: 100%;
  padding: 50px 0 50px 0;
  background: #FAFAFA;
`
const NewsBlock = styled.div`
  width: 48%;
  margin: 0 10px;
