import React from 'react'
import styled from 'styled-components'
import { Container, Row } from 'react-bootstrap'
// import BigTitle from '../bigTitle'
import ThemeCard from './themeCard'

const MyJob = styled.div`
  background: #fff;
  @media (max-width: 768px){
    margin-top: 30px;
  }
`

const Title = styled.div`
  text-align: center;
  padding-bottom: 10px;
  color: #1E1E1E;
`
const TitleH3 = styled.h3`
  display: block;
  line-height: 40px;
  font-size: 22px;
  font-weight: 400;
  @media (max-width: 768px){
    display: none;
  }
`
const TitleMB = styled.h3`
  font-size: 16px;
  line-height: 30px;
  display: none;
  font-weight: 400;
  @media (max-width: 768px){
    display: block;
  }
`
const BigTitle = styled.div`
  text-align: center;
  // padding: 60px 0;
  padding: 60px 0 40px 0;
  @media (max-width: 768px){
    padding: 20px 0 40px 0;
    padding-bottom: 20px;
  }
`
const Img = styled.img`
  width:25%;
  @media (max-width: 768px){
    width:60%;
  }
`

const BigJob = (props) => {
  return(
		<MyJob>
			<BigTitle>
        <Img src='/static/img/t2_job.png' />
      </BigTitle>
      <Title>
        <TitleH3>
            即使平凡，只要勇敢嘗試，在信義就有機會找到你的非凡<br />
            歡迎熱忱的你加入我們，與我們一起共創最大價值
        </TitleH3>
        <TitleMB>
            即使平凡，只要勇敢嘗試，<br />在信義就有機會找到你的非凡<br />
            歡迎熱忱的你加入我們，<br />與我們一起共創最大價值
        </TitleMB>
      </Title>
      <Container>
        <Row>
          <ThemeCard title='小資獨立幸福首選' list1='小資獨立幸福首選小資獨立幸福首選' list2='小資獨立幸福首選小資獨立幸福' />
          <ThemeCard title='新手爸媽滿滿愛' list1='新手爸媽滿滿愛新手爸媽滿滿愛' list2='新手爸媽滿滿愛' />
          <ThemeCard title='銀髮樂活日式悠閒宅' list1='銀髮樂活日式悠閒宅' list2='銀髮樂活日式悠閒宅' />
          <ThemeCard title='銀髮樂活日式' list1='新手爸媽滿滿愛新手爸媽滿滿愛' list2='銀髮樂活日式悠閒宅' />
        </Row>
      </Container>
		</MyJob>
  )
}

export default BigJob
