import React from 'react'
import styled from 'styled-components'
import { Container, Row } from 'react-bootstrap'
// import BigTitle from '../bigTitle'
import ThemeCard from './themeCard'

const MyJob = styled.div`
  background: #fff;
  @media (max-width: 768px){
    margin-top: 30px;
  }
`

const Title = styled.div`
  text-align: center;
  padding-bottom: 10px;
  color: #1E1E1E;
`
const TitleH3 = styled.h3`
  display: block;
  line-height: 40px;
  font-size: 22px;
  font-weight: 400;
  @media (max-width: 768px){
    display: none;
  }
`
const TitleMB = styled.h3`
  font-size: 16px;
  line-height: 30px;
  display: none;
  font-weight: 400;
  @media (max-width: 768px){
    display: block;
  }
`
const BigTitle = styled.div`
  text-align: center;
  // padding: 60px 0;
  padding: 60px 0 40px 0;
  @media (max-width: 768px){
    padding: 20px 0 40px 0;
    padding-bottom: 20px;
  }
`
const Img = styled.img`
  width:25%;
  @media (max-width: 768px){
    width:60%;
  }
`

const BigJob = (props) => {
  return(
		<MyJob>
			<BigTitle>
        <Img src='/static/img/t2_job.png' />
      </BigTitle>
      <Title>
        <TitleH3>
            即使平凡，只要勇敢嘗試，在信義就有機會找到你的非凡<br />
            歡迎熱忱的你加入我們，與我們一起共創最大價值
        </TitleH3>
        <TitleMB>
            即使平凡，只要勇敢嘗試，<br />在信義就有機會找到你的非凡<br />
            歡迎熱忱的你加入我們，<br />與我們一起共創最大價值
        </TitleMB>
      </Title>
      <Container>
        <Row>
          <ThemeCard title='不動產經紀人' list1='房屋買賣、租賃仲介' list2='不動產諮詢服務' list3='售後客戶服務' />
          <ThemeCard title='行政秘書' list1='電腦文書處理及檔案管理' list2='簡易財務記帳管理' list3='客戶接待、電話接聽語溝通' />
          <ThemeCard title='雲端數據工程師' list1='應用平台規劃與開發' list2='解析客群數位行為' list3='分析數位平台經營數據' />
          <ThemeCard title='行銷企劃' list1='網站活動企劃與流程設計' list2='社群編輯經營' list3='線上、線下行銷活動規劃' />
        </Row>
      </Container>
		</MyJob>
  )
}

export default BigJob
