import React, { Component } from 'react'
import styled from 'styled-components'
// import Link from 'next/link'

const Block = styled.div`
  display: block;
  @media (max-width: 768px){
    display: none;
  }
`
const FooterContainer = styled.div`
  width: 80%;
  margin: 0 auto;
  display: flex;
  justify-content: space-between;
`
const FooterList = styled.div`
  width: 12%;
`

const BgIcon = styled.div`
  width: 100%;
  padding: 0;
  background: #3B4B55;
  display: flex;
  color: #fff;
  text-align: center;
  border-bottom: 1px solid #707070;
`

const Footer = styled.div`
  width: 100%;
  padding: 30px 0;
  color: #fff;
  text-align: center;
  background: #263843;
`

class MyFooter extends Component {
  constructor(){
    super()
    this.state = {
      displayMenu1: false,
      displayMenu2: false,
      displayMenu3: false,
    }
  }
  showDropdownMenu1 = (event) => {
    event.preventDefault();
    this.setState({ displayMenu1: true }, () => {
    document.addEventListener('click', this.hideDropdownMenu);
    });
  }
  showDropdownMenu2 = (event) => {
    event.preventDefault();
    this.setState({ displayMenu2: true }, () => {
    document.addEventListener('click', this.hideDropdownMenu);
    });
  }
  showDropdownMenu3 = (event) => {
    event.preventDefault();
    this.setState({ displayMenu3: true }, () => {
    document.addEventListener('click', this.hideDropdownMenu);
    });
  }
  hideDropdownMenu = () => {
    this.setState({ displayMenu1: false,displayMenu2: false,displayMenu3: false}, () => {
      document.removeEventListener('click', this.hideDropdownMenu);
    });

  }
  render(){
    const coll01 = this.state.displayMenu1 ? 'drowActive' : 'drow' ;
    const coll02 = this.state.displayMenu2 ? 'drowActive' : 'drow' ;
    const coll03 = this.state.displayMenu3 ? 'drowActive' : 'drow' ;
    return(
      <Block>
        <BgIcon className='text-left'>
          <FooterContainer>
            <FooterList className="iconList">
              <div className="footerTitle">
                <span>主題特輯</span>
              </div>
              <h6>主題1</h6>
              <h6>主題2</h6>
              <h6>主題3</h6>
              <h6>主題4</h6>
              <h6>主題5</h6>
              <h6>主題6</h6>
            </FooterList>
            <FooterList className="iconList">
              <div className="footerTitle">
                <span>買賣</span>
              </div>
              <h6>辦公</h6>
              <h6>店面</h6>
              <h6>廠房</h6>
