import React, { Component } from 'react'
import styled from 'styled-components'
import Link from 'next/link'

// const Block = styled.div`
//   display: none;
//   @media (max-width: 768px){
//     display: bolck;
//   }
// `

const BgIcon = styled.div`
  width: 100%;
  padding: 10px 0;
  background: #263843;
  display: flex;
  color: #fff;
  text-align: center;
  border-bottom: 1px solid #707070;
`
const BgIconList = styled.div`
  width: 100%;
  padding: 10px 0;
  height: 200px;
  background: #3B4B55;
  display: flex;
  color: #fff;
  text-align: center;
  border-bottom: 1px solid #707070;
`
const Icon = styled.div`
  width: 35px;
  & > img {
    width: 100%;
  }
`
const IconTwo = styled.div`
  width: 33px;
  & > img {
    width: 100%;
  }
`
const FooterList = styled.div`
  width:100%;
  display: flex;
  justify-content: space-between;
  margin: 10px 0;
`
const FooterTag = styled.div`
  text-align: center;
  width: ${props => props.long ? '35%' : '30%'};
  margin: 10px 0;
`

const FooterTitle = styled.div`
  width:90%;
  margin:0 auto;
  margin-bottom: 50px;
`
const Footer = styled.div`
  width: 100%;
  text-align: center;
  color: #fff;
  & > h5 {
    font-weight: 400;
    margin: 15px 0;
  }
  & > h6 {
    color: #fff;
    font-size: 12px;
    margin: 5px 0;
  }
`
const BtnFooter = styled.div`
  width: 100%;
  display: flex;
  position: fixed;
  bottom:0;
  z-index: 1;
  background: linear-gradient(#00B1FF, #2B6ED2);
  color: #fff;
`
const BtnBottomItem = styled.div`
  width: 33%
  font-size: 18px;
  text-align: center;
  border-left: 1px solid #fff;
  border-right: 1px solid #fff;
`

class MobileFooter extends Component {
  constructor(){
    super()
    
  }

  render(){
    return(
      <div className='mbFooter'>
        <BgIconList>
          <FooterTitle>
            <FooterList>
              <FooterTag className='textCenter'>主題特輯</FooterTag>
              <FooterTag className='textCenter'>買賣</FooterTag>
              <FooterTag className='textCenter'>租賃</FooterTag>
            </FooterList>
            <FooterList>
              <FooterTag className='textCenter'>標售</FooterTag>
              <FooterTag className='textCenter'>新聞與研究</FooterTag>
              <FooterTag className='textCenter'>團隊介紹</FooterTag>
            </FooterList>
            <FooterList>
              <FooterTag className='textCenter'>關於全球</FooterTag>
              <FooterTag long className='textCenter'>集團與社會責任</FooterTag>
              <FooterTag>&nbsp;</FooterTag>
            </FooterList>
          </FooterTitle>
        </BgIconList>
        <BgIcon>
          <Footer>
            <h6>信義全球資產管理股份有限公司</h6>
            <h6>&copy;2019 信義房屋股份有限公司 版權所有</h6>
          </Footer>
        </BgIcon>
        <BtnFooter>
          <BtnBottomItem>
            查找物件
          </BtnBottomItem>
          <BtnBottomItem>
          撥打電話
          </BtnBottomItem>
          <BtnBottomItem>
          快速留言
          </BtnBottomItem>
        </BtnFooter>
      </div>
    )
  }
  
}

export default MobileFooter
